export const ADD_TO_CART = 'ADD_TO_CART';
export const DEL_CART = 'DEL_CART';

export const addCart = (product) => {
  return {
    type: ADD_TO_CART,
    payload: product,
  };
};

export const delCart = (product) => {
  return {
    type: DEL_CART,
    payload: product,
  };
};
