// action.js
export const addCart = (item, type) => {
  return {
    type: "ADDITEM",
    payload: { item, itemType: type }
  };
};

export const delCart = (item, type) => {
  return {
    type: "DELITEM",
    payload: { item, itemType: type }
  };
};
