// src/components/MRIForm.js
import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import emailjs from 'emailjs-com';
import '../styles/bookform.css';

const MRIForm = () => {
  const location = useLocation();
  const { service } = location.state || { service: '' };

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    personnummer: '',
    message: '',
    policyConfirmed: false
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const templateParams = {
      ...formData,
      service: service,
      policyConfirmed: formData.policyConfirmed ? 'Yes' : 'No'
    };

    emailjs.send('service_t43b9l3', 'new_template_id', templateParams, 'jpDNG96sg4J956g8G')
      .then((result) => {
        console.log(result.text);
      }, (error) => {
        console.log(error.text);
      });

    // Reset form
    setFormData({
      name: '',
      email: '',
      personnummer: '',
      message: '',
      policyConfirmed: false
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <h2 className='header-mri'>Kundinformation</h2>
        <p className='font-italic'>Vi använder ditt personnummer för hanteringen av undersökningsinformation och för att hitta din folkbokföringsadress dit vi skickar kallelse och undersökningsresultat.
        Väljer du fakturabetalning skickas din faktura efter ditt besök.</p>
        <label>Service:</label>
        <input type="text" name="service" value={service} readOnly />
      </div>
      <div>
        <label>Name:</label>
        <input type="text" name="name" value={formData.name} onChange={handleChange} required />
      </div>
      <div>
        <label>Email:</label>
        <input type="email" name="email" value={formData.email} onChange={handleChange} required />
      </div>
      <div>
      <label>
          <input type="checkbox" name="policyConfirmed" checked={formData.policyConfirmed} onChange={handleChange} required />
          I confirm the policy
        </label>
        <label>Personnummer <span>ÅÅÅÅMMDDXXXX</span></label>
        <input type="text" name="personnummer" value={formData.personnummer} onChange={handleChange} required />
      </div>
      <div>
        <label>Message:</label>
        <textarea name="message" value={formData.message} onChange={handleChange} required />
      </div>
      <div>
        <label>
          <input type="checkbox" name="policyConfirmed" checked={formData.policyConfirmed} onChange={handleChange} required />
          I confirm the policy
        </label>
      </div>
      <div>
        <button type="submit">Submit</button>
      </div>
    </form>
  );
};

export default MRIForm;
