import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/index.css';

const AnimationBody = () => {
  const [isExpanded, setIsExpanded] = useState(false);
  const navigate = useNavigate();

  const animationData = [
    { id: 1, text: 'MR Höger axel', price: '1000kr' },
    { id: 2, text: 'MR Vänster axel', price: '1000kr' },
    { id: 3, text: 'MR Prostatascreening', price: '2000kr' },
    // Add prices for all services
  ];

  const toggleList = () => {
    setIsExpanded(!isExpanded);
  };

  const handleBook = (service) => {
    navigate(`/mri-booking`, { state: { service } });
  };

  return (
    <div>
      <div className="banner-images-container">
        <div className="banner-image">
          <img src="./assets/Humanbody.png" alt=" 1" className="image-front" />
          {animationData.map((item, index) => (
            <div key={item.id} className={`circle-animation circle-animation${index + 1}`}>
              <div className="pop-up">
                <p className='animation-text'>{item.text}</p>
                <button className='animation-button btn-link' onClick={() => handleBook(item)}>Boka</button>
              </div>
            </div>
          ))}
        </div>
      </div>
      <button className="toggle-button" onClick={toggleList}>
        <span>{isExpanded ? 'Hide' : 'Show'} Prices List</span>
        <span className={`arrow ${isExpanded ? 'expanded' : 'collapsed'}`}>&#9660;</span>
      </button>

      <ul className={`mr-services-list ${isExpanded ? 'expanded' : 'collapsed'}`}>
        {animationData.map(item => (
          <li className='list-element' key={item.id}>
            <div className="mr-service-container">
              <div className='mr-service-info'>
                <p>{item.text}</p>
                <p className='mr-price'>{item.price} <span className='more-link'><a href="http://#">Läs mer</a></span></p>
              </div>
              <button className="btn-primary" onClick={() => handleBook(item)}>Boka nu</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AnimationBody;
